    def __init__(self):
        # Initialize two stacks: input_stack and output_stack
        self.input_stack = []
        self.output_stack = []

    def enqueue(self, item):
        # Add elements to the input_stack
        self.input_stack.append(item)

    def dequeue(self):
        # If the output_stack is empty, transfer all elements from the input_stack to the output_stack
        if not self.output_stack:
